<template>
  <div class="date-picker">
    <input readonly 
      :value="dateText" 
      :disabled="disabled"
      :class="
      [show ? 'focus' : '', 
        type === 'daterange' ? 'range-input' : '',
        inputClass]" 
      :placeholder="placeholder"/>
  </div>
</template>

<script>
import { pad } from './utils'
import { DEFAULT_LOCAL } from './utils/constants'

export default {
  props: {
    value: [String, Date, Array],

    type: {
      type: String,
      defualt: 'date'
    },

    disabled: {
      type: Boolean,
      default: false
    },

    placeholder: {
      type: String,
      default: '请选择日期'
    },

    format: {
      type: String,
      default: 'YYYY-MM-DD'
    },

    rangeSeparator: {
      type: String,
      default: '~'
    },

    clearable: {
      type: Boolean,
      default: false
    },

    disabledDate: {
      type: Function,
      default: () => {
        return false
      }
    },

    showButtons: {
      type: Boolean,
      default: false
    },

    inputClass: [String],

    local: {
      type: Object,
      default: () => (DEFAULT_LOCAL)
    }
  },

  computed: {
    dateText () {
      return this.dates.map(date => this.formatDate(date)).join(` ${this.rangeSeparator} `)
    }
  },

  data () {
    return {
      show: false,
      dates: this.initValue(this.value)
    }
  },

  methods: {
    showDatePicker (e) {
      this.show = this.$el.contains(e.target) && !this.disabled
    },

    initValue (val) {
      const now = new Date()
      if (Array.isArray(val)) {
        return val.length > 1 ? val.map(item => new Date(item)) : [now, now]
      } else {
        return val ? [new Date(val)] : [now]
      }
    },

    formatDate (date, format) {
      const year = date.getFullYear()
      const month = date.getMonth()
      const day = date.getDate()
      const hours24 = date.getHours()
      const hours = hours24 % 12 === 0 ? 12 : hours24 % 12
      const minutes = date.getMinutes()
      const seconds = date.getSeconds()
      const milliseconds = date.getMilliseconds()

      const map = {
        YYYY: year,
        MM: pad(month + 1),
        MMM: this.local.months[month],
        MMMM: this.local.monthsHead[month],
        M: month + 1,
        DD: pad(day),
        D: day,
        HH: pad(hours24),
        H: hours24,
        hh: pad(hours),
        h: hours,
        mm: pad(minutes),
        m: minutes,
        ss: pad(seconds),
        s: seconds,
        S: milliseconds
      }
      return (format || this.format).replace(/Y+|M+|D+|H+|h+|m+|s+|S+/g, str => map[str])
    }
  },

  mounted () {
    document.addEventListener('click', this.showDatePicker, true)
  },

  beforeDestroy () {
    document.removeEventListener('click', this.showDatePicker, true)
  }
}
</script>

